import { useEffect, useState } from "react";
import { useHistory, useLocation } from "react-router-dom";
import DataTable from "react-data-table-component";
import Card from "../components/UI/Card";
import api, { call } from "../api/api";
import { toStandard, toUpperFirstCase } from "../utils/string";

const EditProduct = () => {
  const history = useHistory();

  // Get search params
  const location = useLocation();
  const searchParams = new URLSearchParams(location.search);
  const mode = searchParams.get("mode");
  const productId = searchParams.get("productId");

  // Get product in update mode
  const [product, setProduct] = useState(null);

  useEffect(() => {
    // If is not in update mode, do nothing
    if (!productId) return;

    // Get product by productId
    call(api.product.getById(productId), (data) => setProduct(data.item));
  }, []);

  // Render component
  return (
    <div className="edit-product px-3 py-5">
      <h5>{mode === "add" ? "Add new product" : "Update product"}</h5>
      <Card className="mt-4 p-3">
        <form>
          <div className="row g-3">
            <div className="form-group d-flex flex-column gap-2 col-md-6">
              <label>Product name</label>
              <input type="text" className="form-control" placeholder="Enter the product name . . ." />
            </div>
            <div className="form-group d-flex flex-column gap-2 col-md-6">
              <label>Category</label>
              {/* <input type="text" className="form-control" placeholder="Enter the product category . . ." /> */}
              <select className="form-control" placeholder="">
                <option value="" disabled>
                  Enter the product category . . .
                </option>
                <option value="iphone">Iphone</option>
                <option value="ipad">Ipad</option>
                <option value="airpod">Airpod</option>
                <option value="watch">Watch</option>
                <option value="mouse">Mouse</option>
                <option value="keyboard">Keyboard</option>
                <option value="other">Other</option>
              </select>
            </div>
          </div>
          <div className="row g-3 mt-0">
            <div className="form-group d-flex flex-column gap-2 col-md-6">
              <label>Price</label>
              <input
                type="number"
                className="form-control"
                placeholder="Enter the product price . . ."
                min="0"
                step="1"
              />
            </div>
            <div className="form-group d-flex flex-column gap-2 col-md-6">
              <label>Quantity</label>
              <input
                type="number"
                className="form-control"
                placeholder="Enter the product quantity . . ."
                min="0"
                step="1"
              />
            </div>
          </div>
          <div className="row g-3 mt-0">
            <div className="form-group d-flex flex-column gap-2 col-md-6">
              <label>Short description</label>
              <textarea type="text" className="form-control" placeholder="Enter the short description . . ." rows="5" />
            </div>
            <div className="form-group d-flex flex-column gap-2 col-md-6">
              <label>Long description</label>
              <textarea type="text" className="form-control" placeholder="Enter the long description . . ." rows="5" />
            </div>
          </div>
          {mode === "add" && (
            <div className="form-group d-flex flex-column gap-2 mt-3">
              <label>Product images (up to 5 images)</label>
              <input type="file" className="form-control" multiple />
            </div>
          )}
          <div className="form-group mt-4 text-center">
            <button type="submit" className="btn btn-success px-5 py-2">
              {mode === "add" ? "Add new product" : "Update product"}
            </button>
          </div>
        </form>
      </Card>
    </div>
  );
};

export default EditProduct;
