import { useHistory } from "react-router-dom";
import Card from "../components/UI/Card";
import "./LiveChat.css";

import avatar from "../assets/images/admin_avt.jpg";
import { useState } from "react";
import { useEffect } from "react";
import api, { call } from "../api/api";

const LiveChat = () => {
  const history = useHistory();

  // Rooms list
  const [rooms, setRooms] = useState([]);
  const [currRoom, setCurrRoom] = useState(null);

  // Get all rooms
  useEffect(() => {
    // call(api.room.getAll(), (data) => setRooms(data.items));
  }, []);

  // Render component
  return (
    <div className="live-chat px-3 py-5">
      <h5>Live chat</h5>
      <Card className="mt-4 py-3 row mx-0">
        <div className="live-chat__left col-lg-3 col-md-4 col-5">
          <input className="live-chat__search form-control" type="text" placeholder="Search contact . . ." />
          {rooms.length === 0 && <h5 className="mt-3 text-center">Hiện không có tin nhắn đang chờ nào</h5>}
          {rooms.map((room) => (
            <div className="live-chat__contact" key={room._id}>
              <img src={avatar} alt="avatar" />
              {room._id}
            </div>
          ))}
        </div>
        <div className="live-chat__right col-lg-9 col-md-8 col-7">Chatting</div>
      </Card>
    </div>
  );
};

export default LiveChat;
