import { Fragment } from "react";
import { createPortal } from "react-dom";
import { FaFile, FaSmile, FaPaperPlane } from "react-icons/fa";

import admin from "../assets/images/admin_avt.jpg";
import css from "./LivechatPopup.module.css";

const Modal = () => {
  // Render component
  return (
    <div className={css["livechat-modal"]}>
      <div className={css["livechat-modal__header"]}>
        <div>Customer Support</div>
        <div className="d-none d-sm-block">Let's Chat App</div>
      </div>
      <div className={css["livechat-modal__body"]}>
        <div className={css["user-message"]}>Xin chào</div>
        <div className={css["user-message"]}>
          Làm thế nào để xem các sản phẩm
        </div>
        <div className={css["admin-message"]}>
          <img src={admin} alt="admin" />
          <div>ADMIN: Chào bạn</div>
        </div>
        <div className={css["admin-message"]}>
          <img src={admin} alt="admin" />
          <div>ADMIN: Bạn có thể vào mục Shop để xem các sản phẩm</div>
        </div>
      </div>
      <div className={css["livechat-modal__footer"]}>
        <img src={admin} alt="admin" />
        <form>
          <input type="text" placeholder="Enter Message!" />
          <FaFile />
          <FaSmile />
          <FaPaperPlane />
        </form>
      </div>
    </div>
  );
};

const LivechatPopup = () => {
  return (
    <Fragment>
      {createPortal(<Modal />, document.getElementById("overlay-root"))}
    </Fragment>
  );
};

export default LivechatPopup;
