import { useEffect, useState } from "react";
import DataTable from "react-data-table-component";
import { Link } from "react-router-dom";
import api, { call } from "../api/api";
import Card from "../components/UI/Card";
import { toUpperFirstCase } from "../utils/string";

const Products = () => {
  // Table column options
  const columns = [
    {
      name: "ID",
      selector: (row) => row._id,
      sortable: true,
      center: true,
      grow: 1,
    },
    {
      name: "Name",
      selector: (row) => row.name,
      sortable: true,
      center: true,
      grow: 1,
    },
    {
      name: "Price",
      selector: (row) => `${Number(row.price).toLocaleString()} VND`,
      sortable: true,
      center: true,
      grow: 1,
    },
    {
      name: "Total",
      selector: (row) => <img src={row.imgs[0]} alt="product" />,
      center: true,
      grow: 1,
    },
    {
      name: "Category",
      selector: (row) => toUpperFirstCase(row.category),
      sortable: true,
      center: true,
      grow: 1,
    },
    {
      name: "Edit",
      selector: (row) => (
        <div className="d-flex">
          <Link to={`/products/edit/${row._id}`} className="btn btn-success text-light me-3">
            Update
          </Link>
          <button className="btn btn-danger">Delete</button>
        </div>
      ),
      center: true,
      grow: 1,
    },
  ];

  // Table data state
  const [data, setData] = useState([]);

  // Get orders data
  useEffect(() => {
    call(api.order.getAll(), (resData) => setData(resData.items));
  }, []);

  // Render component
  return (
    <div className="dashboard px-3 py-5">
      <h5>Dashboard</h5>
      <Card className="mt-3 d-flex">
        <div className="dashboard-item">
          <div>
            <h3>2</h3>
            <div>Clients</div>
          </div>
          <FaUserAlt fontSize="1.5rem" color="gray" />
        </div>
        <div className="dashboard-item">
          <div>
            <h3>32</h3>
            <div>Number of orders</div>
          </div>
          <FaFileAlt fontSize="1.5rem" color="gray" />
        </div>
        <div className="dashboard-item">
          <div>
            <h3>32.220.000 VND</h3>
            <div>Earning of month</div>
          </div>
          <FaMoneyBill fontSize="1.5rem" color="gray" />
        </div>
        <div className="dashboard-item">
          <div>
            <h3>30.110.600 VND</h3>
            <div>Average earning</div>
          </div>
          <FaMoneyBillAlt fontSize="1.5rem" color="gray" />
        </div>
      </Card>
      <Card className="mt-4 p-3">
        <h5>History</h5>
        <DataTable className="dashboard-table" columns={columns} data={data} pagination />
      </Card>
    </div>
  );
};

export default Dashboard;
