import { Fragment, useEffect, useState } from "react";
import { useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import api, { call } from "../api/api";

import OrderTable from "../components/pages/order/OrderTable";
import Loading from "./Loading";
import NotLogin from "./NotLogin";
import Card from "../components/UI/Card";
import DataTable from "react-data-table-component";

const Order = () => {
  // Current user
  const user = useSelector((state) => state.user.user);

  // Get orderId
  const orderId = useParams().orderId;

  // Order state
  const [order, setOrder] = useState(null);

  // Get order
  useEffect(() => {
    call(api.order.getById(orderId), (data) => setOrder(data.item));
  }, [orderId]);

  // If user did not login
  if (!user) return <NotLogin />;
  if (!order) return <Loading />;

  // Table props
  const columns = [
    {
      name: "ID",
      selector: (row) => row._id,
      sortable: true,
      center: true,
      hide: "lg",
      grow: 2,
    },
    {
      name: "Image",
      selector: (row) => <img src={row.imgs[0]} alt="product" />,
    },
  ];

  // If user logged in
  return (
    <div className="order-info px-3 py-5">
      <h5>Order information</h5>
      <Card className="mt-3 p-3">
        <div className="mb-2">ID User: {order.user._id}</div>
        <div className="mb-2">Full Name: {order.receiver.full_name}</div>
        <div className="mb-2">Phone Number: {order.receiver.phone_number}</div>
        <div className="mb-2">Address: {order.receiver.address}</div>
        <div>Total: {Number(order.total_price).toLocaleString()} VND</div>
      </Card>
      <h5 className="mt-4">Order</h5>
      <Card className="mt-3 p-3">
        <DataTable />
      </Card>
      <OrderTable order={order} />
    </div>
  );
};

export default Order;
