import { useEffect, useState } from "react";
import { useHistory, useLocation } from "react-router-dom";
import DataTable from "react-data-table-component";
import Card from "../components/UI/Card";
import api, { call } from "../api/api";
import { toStandard, toUpperFirstCase } from "../utils/string";

const EditProduct = () => {
  const history = useHistory();
  // Get search params
  const location = useLocation();
  const searchParams = new URLSearchParams(location.search);
  const mode = searchParams.get("mode");
  const productId = searchParams.get("productId");

  // Table data state
  const [fullData, setFullData] = useState([]);
  const [data, setData] = useState([]);
  const [ipSearch, setIpSearch] = useState("");

  // Get orders data
  useEffect(() => {
    call(api.product.getAll(), (resData) => {
      setFullData(resData.items);
      setData(resData.items);
    });
  }, []);

  // Filter data on search change
  useEffect(() => {
    const tokens = toStandard(ipSearch).split(" ");
    const filtered = fullData.filter((prod) => tokens.every((tok) => toStandard(prod.name).includes(tok)));
    setData(filtered);
  }, [fullData, ipSearch]);

  // Render component
  return (
    <div className="edit-product px-3 py-5">
      <h5>{mode === "add" ? "Add new product" : "Update product"}</h5>
      <Card className="mt-4 p-3">
        <form>
          <div className="row g-3">
            <div className="form-group d-flex flex-column gap-2 col-md-6">
              <label>Product name</label>
              <input type="text" className="form-control" placeholder="Enter the product name . . ." />
            </div>
            <div className="form-group d-flex flex-column gap-2 col-md-6">
              <label>Category</label>
              <input type="text" className="form-control" placeholder="Enter the product category . . ." />
            </div>
          </div>
          <div className="row g-3 mt-0">
            <div className="form-group d-flex flex-column gap-2 col-md-6">
              <label>Price</label>
              <input
                type="number"
                className="form-control"
                placeholder="Enter the product price . . ."
                min="0"
                step="1"
              />
            </div>
            <div className="form-group d-flex flex-column gap-2 col-md-6">
              <label>Quantity</label>
              <input
                type="number"
                className="form-control"
                placeholder="Enter the product quantity . . ."
                min="0"
                step="1"
              />
            </div>
          </div>
          <div className="row g-3 mt-0">
            <div className="form-group d-flex flex-column gap-2 col-md-6">
              <label>Short description</label>
              <textarea type="text" className="form-control" placeholder="Enter the short description . . ." rows="5" />
            </div>
            <div className="form-group d-flex flex-column gap-2 col-md-6">
              <label>Long description</label>
              <textarea type="text" className="form-control" placeholder="Enter the long description . . ." rows="5" />
            </div>
          </div>
          <div className="form-group d-flex flex-column gap-2 mt-3">
            <label>Product images (up to 5 images)</label>
            <input type="file" className="form-control" />
          </div>
          <div className="form-group mt-3">
            <button type="submit" className="btn btn-success mt-3 w-50">
              Submit
            </button>
          </div>
        </form>
      </Card>
    </div>
  );
};

export default EditProduct;
