import { useSelector } from "react-redux";

import HistoryTableRow from "./HistoryTableRow";
import "./HistoryTable.css";
import { useEffect, useState } from "react";
import api, { call } from "../../../api/api";

const HistoryTable = () => {
  // Orders state
  const [orders, setOrders] = useState([]);

  // Get current user
  const user = useSelector((state) => state.user.user);

  // Get orders
  useEffect(() => {
    call(api.order.getAll({ user: user._id }), (data) => setOrders(data.items));
  }, [user._id]);

  // Render component
  return (
    <div className="history-table">
      <div className="row history-table__header">
        <h6 className="col-2 col-md-3">NAME</h6>
        <h6 className="col-1 d-none d-md-flex">PHONE</h6>
        <h6 className="col-2 col-md-3">ADDRESS</h6>
        <h6 className="col-2 col-md-3">TOTAL</h6>
        <h6 className="col-2 d-none d-md-flex">DELIVERY</h6>
        <h6 className="col-2 d-none d-md-flex">STATUS</h6>
        <h6 className="col-1 col-md-3">DETAIL</h6>

        {/* <h6 className="col-3 col-md-2">NAME</h6>
        <h6 className="col-2 col-lg-1 d-none d-md-block">PHONE</h6>
        <h6 className="col-3 col-lg-1 col-md-2">ADDRESS</h6>
        <h6 className="col-3 col-md-1">TOTAL</h6>
        <h6 className="col-2 col-lg-1 d-none d-md-block">DELIVERY</h6>
        <h6 className="col-1 d-none d-md-block">STATUS</h6>
        <h6 className="col-3 col-lg-1 col-md-2">DETAIL</h6> */}
      </div>
      {/* Render orders */}
      {orders.map((order, index) => (
        <HistoryTableRow order={order} key={index} />
      ))}
    </div>
  );
};

export default HistoryTable;
