// Create API constant
const API = {};

// API hostname
const HOSTNAME = "http://localhost:5000";

// Set value for sub-constant
API.POST_REGISTER = HOSTNAME + "/api/auth/register";
API.POST_LOGIN = HOSTNAME + "/api/auth/login";
API.POST_LOGOUT = HOSTNAME + "/api/auth/logout";

export default API;
