import { createSlice } from "@reduxjs/toolkit";
import api from "../../api/api";

let initialState = {
  isLoggedIn: false,
  user: null,
};

// Get session
api.session
  .get()
  // Process data
  .then((res) => (initialState = res.data.item))
  // Catch error
  .catch((err) => alert(err.response.data.message));

const slice = createSlice({
  name: "auth",
  initialState: initialState,
  reducers: {
    login(state, user) {
      // Create session
      api.session
        .create(user)
        // Process data
        .then((res) => {
          alert(res.data.message);
          state.isLoggedIn = true;
          state.user = res.data.user;
        })
        // Catch error
        .catch((err) => alert(err.response.data.message));
    },
    logout(state) {
      // Delete session
      api.session.delete
        // Process data
        .then((res) => {
          alert(res.data.message);
          state.isLoggedIn = false;
          state.user = null;
        })
        // Catch error
        .catch((err) => alert(err.response.data.message));
    },
  },
});

export const authActions = slice.actions;
export default slice.reducer;
