import { createAsyncThunk } from "@reduxjs/toolkit";
import api from "../../api/api";

// Login action
export const login = createAsyncThunk("auth/login", async (account) => {
  // Create new session for this account
  const response = await api.session.create(account);
  return response.data;
});

// Logout action
export const logout = createAsyncThunk("auth/logout", async () => {
  // Create new session for this account
  const response = await api.session.delete();
  return response.data;
});

// Get current user
export const getUser = createAsyncThunk("auth/user", async (userId) => {
  // Create new session for this account
  const response = await api.user.getById(userId);
  return response.data;
});
