export const toStandard = (str) => str.replace(/  +/g, " ").toLowerCase();

export const toUpperFirstCase = (str) => {
  const stdStr = str.toStandard();
  return stdStr.charAt(0).toUpperCase() + stdStr.slice(1);
};
