import { NavLink, useHistory } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { FaShoppingCart, FaUser } from "react-icons/fa";

import { Fragment, useEffect, useState } from "react";
import { authActions } from "../store/auth/reducer";
import Container from "../components/UI/Container";
import css from "./Header.module.css";
import api, { call } from "../api/api";

const Header = () => {
  // Page history
  const history = useHistory();

  // Dispatch function
  const dispatch = useDispatch();

  // Current user
  // const [user, setUser] = useState(null);
  const userId = useSelector((state) => state.auth.userId);

  // // Fetch user info
  // useEffect(() => {
  //   // Get user
  //   if (userId) call(api.user.getById(userId), (data) => setUser(data.item));
  //   else setUser(null);

  //   // Deps
  // }, [userId]);

  // Fetch user info
  useEffect(() => {
    console.log("Id: " + userId);
    console.log("run");
    if (!userId) dispatch(authActions.init());

    // Deps
  }, [dispatch, userId]);

  // Handler when click logout
  const logoutHandler = (e) => {
    e.preventDefault();

    // Logout
    dispatch(authActions.logout())
      .then(() => history.push("/login"))
      .catch((err) => alert(err));
  };

  // Render component
  return (
    <header className={css["header"]}>
      <Container>
        <nav>
          <ul>
            <li className={`${css["logo"]} d-none d-md-block`}>BOUTIQUE</li>
            <li>
              <NavLink exact to="/" activeClassName={css.active}>
                Home
              </NavLink>
            </li>
            <li>
              <NavLink to="/shop" activeClassName={css.active}>
                Shop
              </NavLink>
            </li>
            <li className="spacer" />
            <li>
              <NavLink to="/cart" activeClassName={css.active}>
                <FaShoppingCart /> Cart
              </NavLink>
            </li>
            {userId ? (
              <Fragment>
                <li className="d-none d-sm-block">
                  {/* <FaUser /> {user.full_name} */}
                  <FaUser /> {userId}
                </li>
                <li onClick={logoutHandler}>( Logout )</li>
              </Fragment>
            ) : (
              <li>
                <NavLink to="/login" activeClassName={css.active}>
                  <FaUser /> Login
                </NavLink>
              </li>
            )}
          </ul>
        </nav>
      </Container>
    </header>
  );
};

export default Header;
