import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  items: [],
  resItems: [],
  filter: {
    category: "",
    query: "",
    sort: "",
  },
};

const slice = createSlice({
  name: "product",
  initialState: initialState,
  reducers: {
    setItems(state, action) {
      state.items = action.payload.items;
      state.resItems = action.payload.items;
    },
    setFilter(state, action) {
      // Get payload data
      const category = action.payload.filter.category;
      const query = action.payload.filter.query;
      const sort = action.payload.filter.sort;
      state.filter = action.payload.filter;

      // Temporary for result items
      let tmp = state.items;

      // Filter by category
      if (category !== "")
        tmp = tmp.filter((item) => item.category === category);

      // Filter by query
      if (query !== "")
        tmp = tmp.filter((item) =>
          item.name.toLowerCase().includes(query.toLowerCase())
        );

      // Sort by price
      if (sort !== "")
        tmp.sort((a, b) => {
          if (sort === "price_increase") return a.price - b.price;
          if (sort === "price_decrease") return b.price - a.price;
          return 0;
        });

      // Set result items
      state.resItems = tmp;
    },
  },
});

export const productActions = slice.actions;
export default slice.reducer;
