import { createSlice } from "@reduxjs/toolkit";
import { load, login, logout, save } from "./actions";

// Create slice
const slice = createSlice({
  name: "user",
  initialState: {
    isLoading: false,
    error: null,
    user: null,
  },
  reducers: {
    // changeCartProductQty(state, action) {},
  },
  extraReducers: {
    // Login action
    [login.pending.type]: (state) => {
      state.isLoading = true;
      state.error = null;
    },
    [login.fulfilled.type]: (state, action) => {
      state.isLoading = false;
      state.user = action.payload.user;
      alert(action.payload.message);
    },
    [login.rejected.type]: (state, action) => {
      state.isLoading = false;
      state.error = action.payload.error;
    },
    // Logout action
    [logout.pending.type]: (state) => {
      state.isLoading = true;
      state.error = null;
    },
    [logout.fulfilled.type]: (state, action) => {
      state.isLoading = false;
      state.user = null;
      alert(action.payload.message);
    },
    [logout.rejected.type]: (state, action) => {
      state.isLoading = false;
      state.error = action.payload.error;
    },
    // Load current user
    [load.pending.type]: (state) => {
      state.isLoading = true;
      state.error = null;
    },
    [load.fulfilled.type]: (state, action) => {
      state.isLoading = false;
      state.user = action.payload.user;
    },
    [load.rejected.type]: (state, action) => {
      state.isLoading = false;
      state.error = action.payload.error;
    },
    // Save user
    [save.pending.type]: (state) => {
      state.isLoading = true;
      state.error = null;
    },
    [save.fulfilled.type]: (state, action) => {
      state.isLoading = false;
      state.user = action.payload.user;
    },
    [save.rejected.type]: (state, action) => {
      state.isLoading = false;
      state.error = action.payload.error;
    },
  },
});

export const userActions = { ...slice.actions, login, logout, load, save };
export default slice.reducer;
