import useUrl from "../hooks/useUrl";
import ShopBanner from "../components/pages/shop/ShopBanner";
import CategoryNav from "../components/pages/shop/CategoryNav";
import ProductList from "../components/pages/shop/ProductList";
import Loading from "../pages/Loading";
import { Fragment, useEffect, useState } from "react";

const Shop = () => {
  // All products
  const [items] = useUrl();
  // Loading state
  const [isLoading, setIsLoading] = useState(true);

  // Only render when items is loaded
  useEffect(() => {
    if (!items || items.length === 0) return;
    setIsLoading(false);
    console.log(items);
  }, [items]);

  if (isLoading) return <Loading />;

  // Render component
  return (
    <Fragment>
      {!isLoading && (
        <Fragment>
          <ShopBanner />
          <div className="row mb-5">
            <div className="col-sm-3 col-md-3 mb-4">
              <CategoryNav pathname="/shop" />
            </div>
            <div className="col-sm-9 col-md-9">
              <ProductList items={items} pathname="/shop" />
            </div>
          </div>
        </Fragment>
      )}
    </Fragment>
  );
};

export default Shop;
