import { NavLink, useHistory } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { FaShoppingCart, FaUser } from "react-icons/fa";

import { Fragment } from "react";
import { authActions } from "../store/reducers/auth";
import { cartActions } from "../store/reducers/cart";
import Container from "../components/UI/Container";
import css from "./Header.module.css";

const Header = () => {
  // Page history
  const history = useHistory();

  // Dispatch function
  const dispatch = useDispatch();

  // Current user
  const CURRENT_USER_KEY = "current_user_key";
  const currentUser = JSON.parse(localStorage.getItem(CURRENT_USER_KEY)) || [];

  // Login state
  const isLoggedIn = useSelector((item) => item.auth.isLoggedIn);

  // Handler when click logout
  const logoutHandler = (e) => {
    e.preventDefault();
    dispatch(authActions.logout());
    // Reset cart
    dispatch(cartActions.reset());
    localStorage.removeItem(CURRENT_USER_KEY);
    history.push("/login");
  };

  // Render component
  return (
    <header className={css["header"]}>
      <Container>
        <nav>
          <ul>
            <li className={`${css["logo"]} d-none d-md-block`}>BOUTIQUE</li>
            <li>
              <NavLink exact to="/" activeClassName={css.active}>
                Home
              </NavLink>
            </li>
            <li>
              <NavLink to="/shop" activeClassName={css.active}>
                Shop
              </NavLink>
            </li>
            <li className="spacer" />
            <li>
              <NavLink to="/cart" activeClassName={css.active}>
                <FaShoppingCart /> Cart
              </NavLink>
            </li>
            {isLoggedIn ? (
              <Fragment>
                <li className="d-none d-sm-block">
                  <FaUser /> {currentUser.name}
                </li>
                <li onClick={logoutHandler}>( Logout )</li>
              </Fragment>
            ) : (
              <li>
                <NavLink to="/login" activeClassName={css.active}>
                  <FaUser /> Login
                </NavLink>
              </li>
            )}
          </ul>
        </nav>
      </Container>
    </header>
  );
};

export default Header;
