import { useEffect, useRef } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useHistory, useLocation, useParams } from "react-router-dom";

import ProductItem from "./ProductItem";
import Pagination from "../../UI/Pagination";
import { productActions } from "../../../store/product/product";
import "./ProductList.css";

const ProductList = ({ items }) => {
  // Dispatch function
  const dispatch = useDispatch();

  // Use router hook to get data from url
  const params = useParams();
  const history = useHistory();
  const location = useLocation();
  const searchParams = new URLSearchParams(location.search);
  console.log(location.search);

  // Get data from url
  const category = params.category || "";
  const query = searchParams.get("query") || "";
  const sort = searchParams.get("sort") || "";

  // Set filter when url change
  useEffect(() => {
    dispatch(productActions.setItems({ items }));
    dispatch(productActions.setFilter({ filter: { category, query, sort } }));
  }, [dispatch, category, query, sort, items]);

  // Get products after filter
  const products = useSelector((state) => state.product.resItems);

  // Input reference
  const queryRef = useRef();
  const sortRef = useRef();

  // Handlers for inputs change
  const inputChangeHandler = () => {
    // Get input value
    const query = queryRef.current.value;
    const sort = sortRef.current.value;
    dispatch(productActions.setFilter({ filter: { category, query, sort } }));

    // Create search string
    const search = {};
    if (query !== "") search.query = query;
    if (sort !== "") search.sort = sort;
    const searchString = new URLSearchParams(search).toString();

    // Push new page
    if (searchString === "") history.push(location.pathname);
    else history.push(location.pathname + "?" + searchString);
  };

  // Render component
  return (
    <div className="product-list">
      <div className="product-filter">
        <input
          type="text"
          ref={queryRef}
          defaultValue={query}
          onChange={inputChangeHandler}
          placeholder="Enter Search Here"
        />
        <select onChange={inputChangeHandler} ref={sortRef} defaultValue={sort}>
          <option value="">Default sorting</option>
          <option value="price_increase">Sort by price increase</option>
          <option value="price_decrease">Sort by price decrease</option>
        </select>
      </div>
      <div className="product-items row">
        {/* Render products */}
        {products.map((item) => (
          <div className="col-sm-6 col-md-4" key={item._id.$oid}>
            <ProductItem item={item} />
          </div>
        ))}
      </div>
      {products.length !== 0 && (
        <div className="product-footer">
          <Pagination page="1" pageSize="12" totalItems={products.length} />
        </div>
      )}
    </div>
  );
};

export default ProductList;
