import { useState } from "react";
import { useDispatch, useSelector } from "react-redux";

import Button from "../../UI/Button";
import Counter from "../../UI/Counter";
import { cartActions } from "../../../store/reducers/cart";
import "./DetailInfo.css";

const DetailInfo = ({ item }) => {
  // Dispatch function
  const dispatch = useDispatch();

  // Login state
  const isLoggedIn = useSelector((state) => state.auth.isLoggedIn);

  // Quantity of item
  const [quantity, setQuantity] = useState(0);

  // Handler when change counter
  const changeCounterHandler = (counter) => setQuantity(counter);

  // Handler when add product to cart
  const addProductHandler = () => {
    // If quantity = 0, return
    if (quantity === 0) return;
    // If user did not login
    if (isLoggedIn === false) {
      alert("You must login to use this feature!");
      return;
    }
    // If user logged in
    else {
      dispatch(cartActions.add({ info: item, quantity }));
      setQuantity(0);
      alert("Product added to cart successfully!");
    }
  };

  // Render component
  return (
    <div className="detail-info">
      <h1 className="detail-info__name">{item.name}</h1>
      <h5 className="detail-info__price text--gray">
        {Number(item.price).toLocaleString()} VND
      </h5>
      <div className="detail-info__desc text--gray">{item.short_desc}</div>
      <div className="detail-info__cate text--gray">
        <h6>CATEGORY: </h6>
        {item.category}
      </div>
      <div className="detail-info__cart">
        <label className="text--gray">QUANTITY</label>
        <Counter
          key={Math.random()}
          counter={quantity}
          onChange={changeCounterHandler}
        />
        <Button onClick={addProductHandler}>Add to cart</Button>
      </div>
    </div>
  );
};

export default DetailInfo;
