import { Fragment, useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";

import "./navbar.css";
import API from "../../constants/api";

const Navbar = () => {
  // Login state
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  // Navigate function
  const navigate = useNavigate();

  useEffect(() => {
    // Get login state
    fetch(API.GET_LOGIN, {
      method: "GET",
      credentials: "include",
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((res) => res.json())
      // Set login state
      .then((data) => setIsLoggedIn(data.isLoggedIn));
  }, []);

  // Handle when click login
  const handleLoginClick = () => {
    navigate("/login");
  };

  // Handle when click register
  const handleRegisterClick = () => {
    navigate("/register");
  };

  // Handle when click logout
  const handleLogoutClick = () => {
    fetch(API.POST_LOGOUT, {
      method: "POST",
      credentials: "include",
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((res) => res.json())
      .then((data) => {
        // Alert to user logout state
        alert(data.message);
        // If logout successful, redirect to login page
        navigate("/login");
      })
      .catch((err) => console.log(err));
  };

  return (
    <div className="navbar">
      <div className="navContainer">
        <span className="logo">
          <Link to="/">Booking Website</Link>
        </span>
        <div className="navItems">
          {isLoggedIn && (
            // If user is logged in
            <button className="navButton" onClick={handleLogoutClick}>
              Logout
            </button>
          )}{" "}
          {!isLoggedIn && (
            // If user have not logged in yet
            <Fragment>
              <button className="navButton" onClick={handleRegisterClick}>
                Register
              </button>
              <button className="navButton" onClick={handleLoginClick}>
                Login
              </button>
            </Fragment>
          )}
        </div>
      </div>
    </div>
  );
};

export default Navbar;
