import { useSelector } from "react-redux";

import "./CheckoutTotal.css";

const CheckoutTotal = () => {
  // Get cart items
  const items = useSelector((state) => state.cart.items);

  // Calc sum function
  const calcSum = (items) =>
    items.reduce((sum, item) => sum + item.info.price * item.quantity, 0);

  // Render component
  return (
    <div className="checkout-total p-5">
      <h5 className="mb-4">CART TOTAL</h5>
      {/* Render cart items */}
      {items.map((item, index) => (
        <div className="checkout-total__item row" key={index}>
          <h6 className="col">{item.info.name}</h6>
          <div className="col text--gray">
            {Number(item.info.price).toLocaleString()} VND &times;{" "}
            {Number(item.quantity).toLocaleString()}
          </div>
        </div>
      ))}
      <div className="checkout-total__total">
        <h6>TOTAL</h6>
        <div>{Number(calcSum(items)).toLocaleString()} VND</div>
      </div>
    </div>
  );
};

export default CheckoutTotal;
