import { createAsyncThunk, isRejectedWithValue } from "@reduxjs/toolkit";
import api from "../../api/api";

// Login action
export const login = createAsyncThunk("user/login", async (account, { rejectWithValue }) => {
  try {
    // Create session
    const response = await api.session.create(account);
    return response.data;

    // Catch error
  } catch (error) {
    if (!error.response) throw error;
    const { status, data } = error.response;
    return rejectWithValue({ status, error: data.error });
  }
});

// Logout action
export const logout = createAsyncThunk("user/logout", async () => {
  const response = await api.session.delete();
  return response.data;
});

// Load current user;
export const load = createAsyncThunk("user/load", async () => {
  const response = await api.session.get();
  return response.data;
});

// Save current user;
export const save = createAsyncThunk("user/save", async (arg, { getState }) => {
  const user = getState().user.user;
  const response = await api.user.updateById(user._id, user);
  return response.data;
});
