import { FaAngleDoubleLeft, FaAngleDoubleRight } from "react-icons/fa";

import css from "./Pagination.module.css";

const Pagination = ({ options, setOptions }) => {
  // Render component
  return (
    <div className={css["pagination"]}>
      <nav>
        <button
          className={css["prev"]}
          disabled={options.page_number === 1}
          onClick={() => setOptions({ ...options, page_number: options.page_number - 1 })}
        >
          <FaAngleDoubleLeft />
        </button>
        <button className={css["active"]}>1</button>
        <button
          className={css["next"]}
          disabled={options.page_number === options.page_count}
          onClick={() => setOptions({ ...options, page_number: options.page_number + 1 })}
        >
          <FaAngleDoubleRight />
        </button>
      </nav>
      <div className="text--light">
        Showing {options.page_number * options.page_size - options.page_size + 1} -{" "}
        {Math.min(options.item_count, options.page_number * options.page_size + 1)} of {options.item_count} results
      </div>
    </div>
  );
};

export default Pagination;
