import { useRef } from "react";
import { useDispatch, useSelector } from "react-redux";
import api, { call } from "../../../api/api";
import { userActions } from "../../../store/user/reducer";

import Button from "../../UI/Button";
import "./CheckoutForm.css";

const CheckoutForm = () => {
  // Dispatch function
  const dispatch = useDispatch();

  // Input reference
  const nameRef = useRef();
  const emailRef = useRef();
  const phoneRef = useRef();
  const addressRef = useRef();

  // Get current user
  const user = useSelector((state) => state.user.user);

  // Handler when submit form
  const formSubmitHandler = (e) => {
    e.preventDefault();
    // Get data from input
    const receiver = {
      full_name: nameRef.current.value.trim(),
      email: emailRef.current.value.trim(),
      phone_number: phoneRef.current.value.trim(),
      address: addressRef.current.value.trim(),
    };

    console.log(
      JSON.stringify({
        user,
        receiver,
        delivered: false,
        paid: false,
        products: user.cart.products,
        total_price: user.cart.total_price,
      })
    );

    // Create order
    call(
      api.order.create({
        user,
        receiver,
        delivered: false,
        paid: false,
        products: user.cart.products,
        total_price: user.cart.total_price,
      }),
      // Function to apply data
      (data) => {
        const order = data.item;
        call(api.mail.sendOrder(order));
        alert(data.message);
        dispatch(userActions.clearCart());
        dispatch(userActions.save());
      }
    );

    // Reset input field
    nameRef.current.value = "";
    emailRef.current.value = "";
    phoneRef.current.value = "";
    addressRef.current.value = "";
  };

  // Render component
  return (
    <form className="checkout-form" onSubmit={formSubmitHandler}>
      <div>FULL NAME:</div>
      <input type="text" placeholder="Enter Your Full Name Here!" ref={nameRef} required />
      <div>EMAIL:</div>
      <input type="email" placeholder="Enter Your Mail Here!" ref={emailRef} />
      <div>PHONE NUMBER:</div>
      <input type="tel" ref={phoneRef} placeholder="Enter Your Phone Number Here!" required />
      <div>ADDRESS:</div>
      <input type="text" placeholder="Enter Your Address Here!" ref={addressRef} required />
      <Button type="submit">Place order</Button>
    </form>
  );
};

export default CheckoutForm;

