import { FaArrowRight } from "react-icons/fa";
import "./HistoryTableRow.css";

const HistoryTableRow = ({ order }) => {
  // Render component
  return (
    <div className="row history-table__row">
      <div className="col-3 col-md-2">{order.receiver.full_name}</div>
      <div className="col-2 col-lg-1 d-none d-md-flex">{order.receiver.phone_number}</div>
      <div className="col-3 col-lg-1 col-md-2">{order.receiver.address}</div>
      <div className="col-3 col-md-1">{Number(order.total_price).toLocaleString()} VND</div>
      <div className="col-2 col-lg-1 d-none d-md-flex">{order.delivered ? "Delivered" : "Waiting for progressing"}</div>
      <div className="col-1 d-none d-md-flex">{order.paid ? "Waiting for pay" : "Paid"}</div>
      <div className="col-3 col-lg-1 col-md-2">
        <button className="history-remove-btn">
          View <FaArrowRight />
        </button>
      </div>
    </div>
  );
};

export default HistoryTableRow;
