import { Fragment, useEffect, useState } from "react";
import { createPortal } from "react-dom";
import { FaFile, FaSmile, FaPaperPlane } from "react-icons/fa";
import { io } from "socket.io-client";

import admin from "../assets/images/admin_avt.jpg";
import css from "./LivechatPopup.module.css";

const socket = io("http://localhost:5000");

const Modal = () => {
  // Chat state
  const [messages, setMessages] = useState([]);
  const [message, setMessage] = useState("");

  useEffect(() => {
    // Create room on connection
    socket.on("connect", () => socket.emit("client create room"));
    // Listen on new message
    socket.on("server send message", (newMsg) => setMessages([...messages, newMsg]));
    // Remove room on disconnection
    socket.on("disconnect", () => socket.emit("client destroy room", true));
  }, []);

  // Render component
  return (
    <div className={css["livechat-modal"]}>
      <div className={css["livechat-modal__header"]}>
        <div>Customer Support</div>
        <div className="d-none d-sm-block">Let's Chat App</div>
      </div>
      <div className={css["livechat-modal__body"]}>
        {messages.map((msg, i) => {
          if (msg.from_client)
            return (
              <div className={css["user-message"]} key={i}>
                {msg.text}
              </div>
            );
          return (
            <div className={css["admin-message"]} key={i}>
              <img src={admin} alt="admin" />
              <div>ADMIN: {msg.text}</div>
            </div>
          );
        })}
        <div className={css["user-message"]}>Xin chào</div>
        <div className={css["user-message"]}>Làm thế nào để xem các sản phẩm</div>
        <div className={css["admin-message"]}>
          <img src={admin} alt="admin" />
          <div>ADMIN: Chào bạn</div>
        </div>
        <div className={css["admin-message"]}>
          <img src={admin} alt="admin" />
          <div>ADMIN: Bạn có thể vào mục Shop để xem các sản phẩm</div>
        </div>
      </div>
      <div className={css["livechat-modal__footer"]}>
        <img src={admin} alt="admin" />
        <form>
          <input
            type="text"
            placeholder="Enter Message!"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
          />
          <FaFile />
          <FaSmile />
          <FaPaperPlane />
        </form>
      </div>
    </div>
  );
};

const LivechatPopup = () => {
  return <Fragment>{createPortal(<Modal />, document.getElementById("overlay-root"))}</Fragment>;
};

export default LivechatPopup;
