export const toDDMMYYYY = (date) => {
  const dd = date.getDay() + "";
  const mm = date.getMonth() + 1 + "";
  const yyyy = date.getFullYear() + "";

  return [dd.padStart(2, "0"), mm.padStart(2, "0", yyyy.padStart(4, "0"))].join(
    "/"
  );
};
