import { useRef } from "react";
import Navbar from "../../components/navbar/Navbar";
import API from "../../constants/api";
import "./register.css";

const Register = () => {
  const usernameRef = useRef("");
  const password1Ref = useRef("");
  const password2Ref = useRef("");
  const nameRef = useRef("");
  const phoneRef = useRef("");
  const emailRef = useRef("");

  // Validate function
  const validate = () => {
    // Get input value from field
    const username = usernameRef.current.value.trim();
    const password1 = usernameRef.current.value.trim();
    const password2 = usernameRef.current.value.trim();
    const name = usernameRef.current.value.trim();
    const phone = usernameRef.current.value.trim();
    const email = usernameRef.current.value.trim();

    // If
  };

  // Handle when submit login form
  const handleSubmitLogin = (event) => {
    // Prevent page reload when submit form
    event.preventDefault();

    // Create request payload
    const payload = {
      username: usernameRef.current.value.trim(),
      password1: usernameRef.current.value.trim(),
      password2: usernameRef.current.value.trim(),
      name: usernameRef.current.value.trim(),
      phone: usernameRef.current.value.trim(),
      email: usernameRef.current.value.trim(),
    };

    // Post login request
    fetch(API.POST_LOGIN, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(payload),
    })
      .then((res) => res.json())
      .then((data) => console.log(data));
  };

  return (
    <div>
      <Navbar />
      <div className="registerContainer">
        <form className="registerForm">
          <h1 className="registerForm__title">Register</h1>
          <input
            type="text"
            placeholder="Enter username"
            ref={usernameRef}
            required
          />
          <input
            type="password"
            placeholder="Enter password"
            ref={password1Ref}
            required
          />
          <input
            type="password"
            placeholder="Retype password"
            ref={password2Ref}
            required
          />
          <input
            type="text"
            placeholder="Enter your full name"
            ref={nameRef}
            required
          />
          <input
            type="text"
            placeholder="Enter your phone number"
            ref={phoneRef}
            required
          />
          <input
            type="email"
            placeholder="Enter your email"
            ref={emailRef}
            required
          />
          <button type="submit">Register</button>
        </form>
      </div>
    </div>
  );
};

export default Register;
