import { useRef } from "react";
import Navbar from "../../components/navbar/Navbar";
import API from "../../constants/api";
import "./register.css";

const Register = () => {
  const usernameRef = useRef("");
  const password1Ref = useRef("");
  const password2Ref = useRef("");
  const nameRef = useRef("");
  const phoneRef = useRef("");
  const emailRef = useRef("");

  // Handle when submit login form
  const handleSubmitLogin = (event) => {
    // Prevent page reload when submit form
    event.preventDefault();

    // Get input value from field
    const email = emailRef.current.value.trim();
    const password = passwordRef.current.value.trim();

    // Validate
    if (email === "" || password === "") {
      alert("Missing login info. Try again!");
      return;
    }

    // Create request payload
    const payload = { email, password };

    // Post login request
    fetch(API.POST_LOGIN, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(payload),
    })
      .then((res) => res.json())
      .then((data) => console.log(data));
  };

  return (
    <div>
      <Navbar />
      <div className="registerContainer">
        <form className="registerForm">
          <h1 className="registerForm__title">Register</h1>
          <input type="text" placeholder="Enter username" ref={usernameRef} />
          <input
            type="password"
            placeholder="Enter password"
            ref={password1Ref}
          />
          <input
            type="password"
            placeholder="Retype password"
            ref={password2Ref}
          />
          <input type="text" placeholder="Enter your full name" ref={nameRef} />
          <input
            type="text"
            placeholder="Enter your phone number"
            ref={phoneRef}
          />
          <input type="email" placeholder="Enter your email" ref={emailRef} />
          <button type="submit">Register</button>
        </form>
      </div>
    </div>
  );
};

export default Register;
