import { useParams } from "react-router-dom";
import { Fragment, useEffect, useState } from "react";

import RelateProduct from "../components/pages/detail/RelateProduct";
import Description from "../components/pages/detail/Description";
import DetailImage from "../components/pages/detail/DetailImage";
import DetailInfo from "../components/pages/detail/DetailInfo";
import useUrl from "../hooks/useUrl";

const Detail = () => {
  // Get product id
  const params = useParams();
  const productId = params.productId;

  // Get all items
  const [items] = useUrl();

  // Loading state of item
  const [isLoading, setIsLoading] = useState(true);

  // Selected item
  const [item, setItem] = useState();

  // Only render view when item is loaded
  useEffect(() => {
    // Convert all items to selected item
    const convert = (items) =>
      items.find((item) => item._id.$oid === productId);
    // If items is loaded
    if (!items || items.length === 0) return;
    // Set item
    setItem(convert(items));
    setIsLoading(false);
  }, [items, productId]);

  // Render component
  return (
    <Fragment>
      {!isLoading && (
        <Fragment>
          <div className="row mt-5">
            <div className="col-md-6 mb-5">
              <DetailImage item={item} key={Math.random()} />
            </div>
            <div className="col-md-6 mb-5">
              <DetailInfo item={item} />
            </div>
          </div>
          <Description item={item} />
          <RelateProduct items={items} item={item} />
        </Fragment>
      )}
    </Fragment>
  );
};

export default Detail;
