import { useRef } from "react";
import { useHistory } from "react-router-dom";

import Button from "../../UI/Button";
import "./RegisterForm.css";

const RegisterForm = () => {
  // Pages history
  const history = useHistory();

  // User accounts
  const USERS_KEY = "users_key";
  const usersArr = JSON.parse(localStorage.getItem(USERS_KEY)) || [];

  // Input reference
  const nameRef = useRef();
  const emailRef = useRef();
  const passRef = useRef();
  const phoneRef = useRef();

  // Validate data
  const validate = (user) => {
    // If name input is blank
    if (user.name === "") {
      alert("You need to enter full name to create account!");
      return false;
    }

    // If email input is blank
    if (user.email === "") {
      alert("You need to enter email to create account!");
      return false;
    }

    // If password input is blank
    if (user.password === "") {
      alert("You need to enter password to create account!");
      return false;
    }

    // If phone input is blank
    if (user.phone === "") {
      alert("You need to enter phone number to create account!");
      return false;
    }

    // If email already exists
    if (usersArr.filter((acc) => acc.email === user.email).length !== 0) {
      alert("Your email already exists!");
      return false;
    }

    // If password length <8
    if (user.password.length < 8) {
      alert("Valid password must be more than 8 characters!");
      return false;
    }

    // If input data valid
    return true;
  };

  // Handler when submit register form
  const registerHandler = (e) => {
    e.preventDefault();

    // Get input data
    const user = {
      name: nameRef.current.value.trim(),
      email: emailRef.current.value.trim(),
      password: passRef.current.value,
      phone: phoneRef.current.value.trim(),
      items: [],
    };

    // Validate data
    if (validate(user)) {
      // Save usersArr
      usersArr.push(user);
      localStorage.setItem(USERS_KEY, JSON.stringify(usersArr));
      // Alert
      alert("Successful account registration!");
      history.push("/login");
    }
  };

  // Render component
  return (
    <form className="register-form" onSubmit={registerHandler}>
      <div className="input-group">
        <label className="input-group-text">Full Name</label>
        <input type="text" className="form-control" ref={nameRef} />
      </div>
      <div className="input-group">
        <label className="input-group-text">Email</label>
        <input type="email" className="form-control" ref={emailRef} />
      </div>
      <div className="input-group">
        <label className="input-group-text">Password</label>
        <input type="password" className="form-control" ref={passRef} />
      </div>
      <div className="input-group last">
        <label className="input-group-text">Phone</label>
        <input
          type="tel"
          ref={phoneRef}
          className="form-control"
          placeholder="Enter Your Phone Number Here!"
        />
      </div>
      <Button>SIGN UP</Button>
    </form>
  );
};

export default RegisterForm;
