import { createSlice } from "@reduxjs/toolkit";
import { load, login, logout, save } from "./actions";

// Create slice
const slice = createSlice({
  name: "user",
  initialState: {
    isLoading: false,
    error: null,
    user: null,
  },
  reducers: {
    changeCartProductQty(state, action) {
      // Get action payload
      const product = action.payload.product;
      const qty = action.payload.qty;

      // Get cart
      const user = state.user;
      console.log(user);
      // const cart = user.cart;

      // // Find product in current cart
      // const existedProd = suser.cart.products.find(prod=>prod._id===product._id)
    },
  },
  extraReducers: {
    // Login action
    [login.pending]: (state) => {
      state.isLoading = true;
      state.error = null;
    },
    [login.fulfilled]: (state, action) => {
      state.isLoading = false;
      state.user = action.payload.user;
      alert(action.payload.message);
    },
    [login.rejected]: (state, action) => {
      state.isLoading = false;
      state.error = action.payload.error;
    },
    // Logout action
    [logout.pending]: (state) => {
      state.isLoading = true;
      state.error = null;
    },
    [logout.fulfilled]: (state, action) => {
      state.isLoading = false;
      state.user = null;
      alert(action.payload.message);
    },
    [logout.rejected]: (state, action) => {
      state.isLoading = false;
      state.error = action.payload.error;
    },
    // Load current user
    [load.pending]: (state) => {
      state.isLoading = true;
      state.error = null;
    },
    [load.fulfilled]: (state, action) => {
      state.isLoading = false;
      state.user = action.payload.user;
    },
    [load.rejected]: (state, action) => {
      state.isLoading = false;
      state.error = action.payload.error;
    },
    // Save user
    [save.pending]: (state) => {
      state.isLoading = true;
      state.error = null;
    },
    [save.fulfilled]: (state, action) => {
      state.isLoading = false;
      state.user = action.payload.user;
    },
    [save.rejected]: (state, action) => {
      state.isLoading = false;
      state.error = action.payload.error;
    },
  },
});

export const userActions = { ...slice.actions, login, logout, load, save };
export default slice.reducer;
