import { useState } from "react";
import { useDispatch } from "react-redux";
import { FaTrashAlt } from "react-icons/fa";

import Counter from "../../UI/Counter";
import { cartActions } from "../../../store/reducers/cart";
import "./CartTableRow.css";

const CartTableRow = ({ info, quantity }) => {
  // Dispatch function
  const dispatch = useDispatch();

  // Quantity of item
  const [productNum, setProductNum] = useState(quantity);

  // Handler when change counter
  const changeCounterHandler = (counter) => {
    setProductNum(counter);
    // dispatch(cartActions.update({ itemId: info._id.$oid, quantity: counter }));
  };

  // Handler when remove item
  const removeItemHandler = () => {
    // Ask user when remove item
    if (window.confirm("Are you sure you want to remove this item? ") === true) {
      // dispatch(cartActions.delete({ itemId: info._id.$oid }));
    } else return;
  };

  // Render component
  return (
    <div className="row cart-table__row">
      <div className="col-1 d-none d-lg-block">
        <img src={info.img1} alt="product" />
      </div>
      <div className="col-5 col-lg-3 ">
        <h6>{info.name}</h6>
      </div>
      <div className="col-3 col-lg-2 text--gray">{Number(info.price).toLocaleString()} VND</div>
      <div className="col-4 col-lg-2">
        <Counter counter={productNum} onChange={changeCounterHandler} />{" "}
      </div>
      <div className="col-2 d-none d-lg-block text--gray">{Number(info.price * productNum).toLocaleString()} VND</div>
      <div className="col-2 d-none d-lg-block">
        <button onClick={removeItemHandler} className="cart-remove-btn">
          <FaTrashAlt />
        </button>
      </div>
    </div>
  );
};

export default CartTableRow;
