import { createSlice } from "@reduxjs/toolkit";
import { getUser, login, logout } from "./actions";

// Get, set, delete user id in localStorage
const key = "key_uid";
const loadUserId = () => JSON.parse(localStorage.getItem(key));
const saveUserId = (id) => localStorage.setItem(key, JSON.stringify(id));
const deleteUserId = () => localStorage.removeItem(key);

// Get userId from localStorage
const userId = loadUserId();

// Create slice
const slice = createSlice({
  name: "auth",
  initialState: {
    isLoading: false,
    error: null,
    user: null,
    userId: userId,
  },
  reducers: {},
  extraReducers: (builder) =>
    builder
      // Login action
      .addCase(login.pending, (state) => {
        state.isLoading = true;
        state.error = null;
      })
      .addCase(login.fulfilled, (state, action) => {
        state.isLoading = false;
        state.userId = action.payload.userId;
        saveUserId(action.payload.userId);
        alert(action.payload.message);
      })
      .addCase(login.rejected, (state, action) => {
        state.isLoading = false;
        state.error = action.payload.error;
      })
      // Logout action
      .addCase(logout.pending, (state) => {
        state.isLoading = true;
        state.error = null;
      })
      .addCase(logout.fulfilled, (state, action) => {
        state.isLoading = false;
        state.userId = null;
        deleteUserId();
        alert(action.payload.message);
      })
      .addCase(logout.rejected, (state, action) => {
        state.isLoading = false;
        state.error = action.payload.error;
      })
      // Get current user
      .addCase(getUser.pending, (state) => {
        state.isLoading = true;
        state.error = null;
      })
      .addCase(getUser.fulfilled, (state, action) => {
        state.isLoading = false;
        state.user = action.payload.item;
      })
      .addCase(getUser.rejected, (state, action) => {
        state.isLoading = false;
        state.error = action.payload.error;
      }),
});

export const authActions = { ...slice.actions, login, logout, getUser };
export default slice.reducer;
