import "./booking.css";
import Navbar from "../../components/navbar/Navbar";
import Header from "../../components/header/Header";
import MailList from "../../components/mailList/MailList";
import Footer from "../../components/footer/Footer";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faCircleArrowLeft,
  faCircleArrowRight,
  faCircleXmark,
  faLocationDot,
} from "@fortawesome/free-solid-svg-icons";
import { useEffect, useState } from "react";
import { useLocation, useParams } from "react-router-dom";
import API from "../../constants/api";
import { DateRange } from "react-date-range";

const Booking = () => {
  const [item, setItem] = useState({});
  const [date, setDate] = useState([
    {
      startDate: new Date(),
      endDate: new Date(),
      key: "selection",
    },
  ]);

  const params = useParams();
  const hotelId = params.hotelId;

  return (
    <div>
      <Navbar />
      <Header type="list" />
      <div className="bookingContainer">
        <div className="bookingWrapper">
          <div className="bookingTop">
            <div className="bookingTopLeft">
              <h1 className="bookingTitle">This is hotel title</h1>
              <span className="bookingDesc">This is hotel description</span>
            </div>
            <div className="bookingTopRight">
              <div className="bookingPrice">
                <h2>
                  <b>From ${item.cheapestPrice}</b> (1 night)
                </h2>
                <button>Reserve or Book Now!</button>
              </div>
            </div>
          </div>
          <div className="bookingInput">
            <div className="bookingInputDate">
              <h1 className="bookingTitle">Dates</h1>
              <DateRange
                onChange={(item) => setDate([item.selection])}
                minDate={new Date()}
                ranges={date}
              />
            </div>
            <div className="bookingInputInfo">
              <h1 className="bookingTitle">Reserve info</h1>
              <label for="bookingInputName">Your Full Name:</label>
              <input type="email" id="bookingInputName" />
              <label for="bookingInputEmail">Your Email:</label>
              <input type="text" id="bookingInputEmail" />
              <label for="bookingInputPhone">Your Phone Number:</label>
              <input type="text" id="bookingInputPhone" />
              <label for="bookingInputCard">Your Identity Card:</label>
              <input type="text" id="bookingInputCard" />
            </div>
            <div className="bookingRooms"></div>
            <div className="bookingBill"></div>
            <div className="bookingReserve"></div>
          </div>
        </div>
      </div>
      <MailList />
      <Footer />
    </div>
  );
};

export default Booking;
