import "./tranList.css";
import Navbar from "../../components/navbar/Navbar";
import Header from "../../components/header/Header";
import { useContext, useEffect, useState } from "react";
import API from "../../constants/api";
import { AuthContext } from "../../contexts/AuthContext";

const TranList = () => {
  const [items, setItems] = useState([]);

  const { user } = useContext(AuthContext);

  useEffect(() => {
    fetch(API.GET_TRANSACTION_BY_HOTEL_ID + `/${user._id.toString()}`)
      .then((res) => res.json())
      .then((data) => {
        console.log(data);
        setItems(data.items);
      })
      .catch((err) => console.log(err));
  }, []);

  return (
    <div>
      <Navbar />
      <Header type="list" />
      <div className="tranListContainer">
        <div className="tranListWrapper">
          <h1 className="tranListTitle">Your Transactions</h1>
          <table className="tranListTable">
            <tbody>
              <tr className="tranListTableHeader">
                <th>#</th>
                <th>Hotel</th>
                <th>Room</th>
                <th>Date</th>
                <th>Price</th>
                <th>Payment Method</th>
                <th>Status</th>
              </tr>
              {items.map((tran, index) => (
                <tr>
                  <th>{index}</th>
                  <td>This is hotel name</td>
                  <td>{tran.rooms.join(", ")}</td>
                  <td>{typeof tran.startDate}</td>
                  <td>${tran.price}</td>
                  <td>{tran.payment}</td>
                  <td>
                    {tran.status === "Booking" && (
                      <span className="status booking">Booking</span>
                    )}
                    {tran.status === "Check in" && (
                      <span className="status checkIn">Check in</span>
                    )}
                    {tran.status === "Check out" && (
                      <span className="status checkOut">Check out</span>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default TranList;
