          phone_number: "0869388200",
          address: "66 ngõ 75 Giải Phóng",
        },
        products: [
          {
            product: {
              _id: {
                $oid: "62ccd6d75eefc71539bb6b50",
              },
              category: "iphone",
              imgs: [
                "https://firebasestorage.googleapis.com/v0/b/funix-way.appspot.com/o/xSeries%2FCCDN%2FReactJS%2FAssignment_Images%2FASM03_Resources%2Fiphone_12_3.jpeg?alt=media&token=56832bd5-c510-4760-923c-fae236c6712c",
