import { useRef } from "react";

import Button from "../../UI/Button";
import "./CheckoutForm.css";

const CheckoutForm = () => {
  // Input reference
  const nameRef = useRef();
  const emailRef = useRef();
  const phoneRef = useRef();
  const addressRef = useRef();

  // Validate data
  const validate = (data) => {
    // If input name is blank
    if (data.name === "") {
      alert("You must enter your name to place order!");
      return false;
    }

    // If input email is blank
    if (data.email === "") {
      alert("You must enter your email to place order!");
      return false;
    }

    // If input phone is blank
    if (data.phone === "") {
      alert("You must enter your phone number to place order!");
      return false;
    }

    // If input address is blank
    if (data.address === "") {
      alert("You must enter your address to place order!");
      return false;
    }

    // If all input is valid
    return true;
  };

  // Handler when submit form
  const formSubmitHandler = (e) => {
    e.preventDefault();
    // Get data from input
    const data = {
      name: nameRef.current.value.trim(),
      email: emailRef.current.value.trim(),
      phone: phoneRef.current.value.trim(),
      address: addressRef.current.value.trim(),
    };

    if (validate(data)) {
      alert("Order placed successfully!");
      // Reset input field
      nameRef.current.value = "";
      emailRef.current.value = "";
      phoneRef.current.value = "";
      addressRef.current.value = "";
    }
  };

  // Render component
  return (
    <form className="checkout-form" onSubmit={formSubmitHandler}>
      <div>FULL NAME:</div>
      <input
        type="text"
        placeholder="Enter Your Full Name Here!"
        ref={nameRef}
      />
      <div>EMAIL:</div>
      <input type="email" placeholder="Enter Your Mail Here!" ref={emailRef} />
      <div>PHONE NUMBER:</div>
      <input
        type="tel"
        ref={phoneRef}
        placeholder="Enter Your Phone Number Here!"
      />
      <div>ADDRESS:</div>
      <input
        type="text"
        placeholder="Enter Your Address Here!"
        ref={addressRef}
      />
      <Button>Place order</Button>
    </form>
  );
};

export default CheckoutForm;
